<#.SYNOPSIS   Pull back Drive Space info of remote servers.DESCRIPTION   Allows users to get drive space info for remote servers .PARAMETER Server   Name of the server to connect to.PARAMTER AlertsOnly    Optional switch parameter to show only drives below alert threshold.PARAMETER AlerThreshold    Percent of drive space to alert at, default is 10.EXAMPLE   Get-DiskSpaceInfo -Server TestServer03 -AlertsOnly -AlertThreshold 15#>Function Get-DiskSpaceInfo {	[CmdletBinding()]	Param ([Parameter(Mandatory=$True,Position=1)][String]$Server			,[Parameter(Mandatory=$False,Position=2)][Switch]$AlertsOnly			,[Parameter(Mandatory=$False,Position=3)][INT]$AlertThreshold=10	)	Write-Verbose "WMI Call"	$DriveSpace = Get-WmiObject Win32_LogicalDisk -ComputerName $Server -Filter "DriveType<>5" | Select-Object DeviceID, VolumeName, @{Name="TotalSpace";Expression={[MATH]::Round($_.Size/1GB, 2)}}, @{Name="FreeSpace";Expression={[MATH]::Round($_.FreeSpace/1GB, 2)}}, @{Name="PercentFreeSpace";Expression={[MATH]::Round(($_.FreeSpace/$_.Size)*100, 2)}}			IF ($AlertsOnly -eq $False)	{		$DriveSpace	}	ELSE	{		$DriveSpace | Where-Object {$_.PercentFreeSpace -lt $AlertThreshold}	}}